import os


class CognitoSettings:

    def __init__(self):

        required_environment = ['COGNITO_REGION',
                                'COGNITO_POOL_ID',
                                'COGNITO_APP_CLIENT_ID']
        for key in required_environment:
            if key not in os.environ:
                raise EnvironmentError(f"Missing required environment variable {key}")

        self.idp_url: str = f"https://cognito-idp.{os.environ.get('COGNITO_REGION')}" \
                            f".amazonaws.com/{os.environ.get('COGNITO_POOL_ID')}"

        self.client_id: str = os.environ.get("COGNITO_APP_CLIENT_ID")

        self.pool_id: str = os.environ.get("COGNITO_POOL_ID")
