from typing import List, Optional

from tailor.internal.domain.postgres import BaseRepository
from tailor.internal.domain.postgres.models import ProjectDao


class ProjectRepository(BaseRepository):

    def __init__(self, session=None):
        super().__init__()
        if session:
            #  Used for tests
            self._session = session

    def find_by_id(self, db_id: int) -> ProjectDao:
        return self._session.query(ProjectDao).filter_by(id=db_id).first()

    def insert(self, entity: ProjectDao) -> ProjectDao:
        self._session.add(entity)
        self._session.flush()
        return entity  # with db id

    def find_by_name(self, name: str) -> List[ProjectDao]:
        return self._session.query(ProjectDao).filter_by(name=name).all()

    def find_by_uuid(self, uuid: str) -> Optional[ProjectDao]:
        return self._session.query(ProjectDao).filter_by(uuid=uuid).first()

    def find_all(self) -> List[ProjectDao]:
        return self._session.query(ProjectDao).all()
