# -*- coding: utf-8 -*-
from tailor.internal.domain.postgres import BaseRepository
from tailor.internal.domain.postgres.models import TaskDefinitionDao


class TaskDefinitionRepository(BaseRepository):

    def __init__(self, session=None):
        super().__init__()
        if session:
            #  Used for tests
            self._session = session

    def find_by_id(self, entity_id: int) -> TaskDefinitionDao:
        return self._session.query(TaskDefinitionDao).filter_by(id=entity_id).first()

    def find_by_name(self, name: str) -> TaskDefinitionDao:
        return self._session.query(TaskDefinitionDao).filter_by(name=name).first()

    def insert(self, entity: TaskDefinitionDao) -> TaskDefinitionDao:
        self._session.add(entity)
        self._session.flush()
        return entity  # with db id
