# -*- coding: utf-8 -*-
from typing import List

from tailor.internal.domain.postgres import BaseRepository
from tailor.internal.domain.postgres.models import UserDao, UserIdProvider


class UserRepository(BaseRepository):

    def __init__(self, session=None):
        super().__init__()
        if session:
            #  Used for tests
            self._session = session

    def find_by_id(self, entity_id: int) -> UserDao:
        return self._session.query(UserDao).filter_by(id=entity_id).first()

    def find_by_username(self, username: str) -> UserDao:
        return self._session.query(UserDao) \
            .filter_by(username=username,
                       id_provider=UserIdProvider.COGNITO_TAILOR).first()

    def insert(self, entity: UserDao) -> UserDao:
        self._session.add(entity)
        self._session.flush()
        return entity  # with db id

    def find_all(self) -> List[UserDao]:
        return self._session.query(UserDao).filter_by().all()

